 	SELECT NSEQ.NEXTVAL FROM DUAL

 INSERT INTO NOTICE(    NNO  
					       , NTITLE  
					       , NCONTENT 
					       , NWRITER  )
               VALUES(    #{nno}
              		   , #{ntitle}
              		   , #{ncontent}
              		   , #{nwriter}  )

mapper.update:
UPDATE NOTICE
   SET NTITLE    =   #{ntitle},
   	   NCONTENT  =   #{ncontent}
 WHERE NNO = #{nno}

UPDATE NFILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

UPDATE NOTICE SET
