mapper.insert:

 	SELECT NSEQ.NEXTVAL FROM DUAL

 INSERT INTO NOTICE(    NNO  
					       , NTITLE  
					       , NCONTENT 
					       , NWRITER 
					       , gdsImg
					       , gdsThumbImg )
               VALUES(    #{nno}
              		   , #{ntitle}
              		   , #{ncontent}
              		   , #{nwriter}
              		   , #{gdsImg}
              		   , #{gdsThumbImg}  )

mapper.select:
SELECT NNO, NTITLE, NCONTENT, NWRITER, REGDATE, NHIT
FROM ( SELECT NNO, NTITLE, NCONTENT, NWRITER, REGDATE, NHIT, ROW_NUMBER() OVER(ORDER BY NNO DESC) AS RNUM FROM NOTICE WHERE 1=1
) MP WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY NNO DESC

mapper.update:
		UPDATE NOTICE
		SET NTITLE    =   #{ntitle},
		   	  NCONTENT  =   #{ncontent},
		   	  gdsImg = #{gdsImg},
         	  gdsThumbImg = #{gdsThumbImg}
		 WHERE NNO = #{nno}

UPDATE NOTICE SET
NHIT = NHIT+1
WHERE NNO = #{nno}
